(function(){tinymce.PluginManager.requireLangPack('gsynuhimgupload');tinymce.create('tinymce.plugins.GsImUpPlugin',{init:function(ed,url){ed.addCommand('gsynuhimgupload',function(){ed.windowManager.open({file:url+'/dialog.htm',width:320+parseInt(ed.getLang('gsynuhimgupload.delta_width',0)),height:80+parseInt(ed.getLang('gsynuhimgupload.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'});});ed.addButton('gsynuhimgupload',{title:'gsynuhimgupload.desc',cmd:'gsynuhimgupload',image:url+'/img/addimage.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('gsynuhimgupload',n.nodeName=='IMG');});},createControl:function(n,cm){return null;},getInfo:function(){return{longname:'Gsynuh Simple Image Uploader',author:'Thomas Lefevre',authorurl:'',infourl:'http://www.gsynuh-labs.com',version:"1.0"};}});tinymce.PluginManager.add('gsynuhimgupload',tinymce.plugins.GsImUpPlugin);})();